interface NavButtonTypes {
  active: string;
  bubble?: number;
  iconFilled: React.ReactNode;
  iconOutlined: React.ReactNode;
  label: string;
  setActive(label: string): void;
}

const NavButton = ({
  active,
  bubble,
  label,
  iconFilled,
  iconOutlined,
  setActive}: NavButtonTypes) => {
  return (
    <button className="text-center space-y-2 group" onClick={() => setActive(label)}>
      <div
        className={clsx(
          "relative px-5 rounded-full py-1 transition-all",
          active === label ? "bg-blue-300/50" : "group-hover:bg-slate-300/50"
        )}
      >
        {label !== active && bubble && (
          <div className="absolute -top-2 size-4 rounded-full text-white right-2 flex items-center justify-center bg-red-700 text-xs p-1">
            {bubble}
          </div>
        )}

        {active === label ? iconFilled : iconOutlined}
      </div>
      <p className={clsx("text-sm", active === label && "font-semibold")}>{label}</p>
    </button>
  );
};

export default App;
